import React from "react";
import "./css/PlayFilters.css";

const PlayFilters = (props) => {
    const handleOnChange = (e) => {
        const {name, value} = e.target;
        props.setFilters(name, value);

        //toggle checkbox values
        if (e.target.type === "checkbox") {
            if (e.target.value === "on") { e.target.value = "off"; }
            else { e.target.value = "on"; }
        } 
    }

    const handleFilterClick = () => {
        props.setFilteredPlays();
    }

    const handleClearClick = () => {
        props.setFilteredPlays({}, null);
    }

    return (
        <div className="play-filters">
            <form className="filter-form">
                <h1 className="header">Play Filters</h1>
                <label className="main">Title</label><br/>
                <input className="full" name="title" type="text" onChange={handleOnChange} value={props.filters.title}/><br/>
                <label className="main">Year</label><br/>
                <div className="before-filters">
                    <input name="before" type="checkbox" onChange={handleOnChange}/><label className="sub">Before:</label>
                    <input className="partial" name="beforeYear" type="text" onChange={handleOnChange} value={props.filters.beforeYear}/>
                </div>
                <div className="after-filters">
                    <input name="after" type="checkbox" onChange={handleOnChange}/><label className="sub">After:</label>
                    <input className="partial" name="afterYear" type="text" onChange={handleOnChange} value={props.filters.afterYear}/>
                </div>
                <label className="main">Genre</label><br/>
                <select name="genre" onChange={handleOnChange} value={props.filters.genre}>
                    {props.genres.map((g, i) => 
                        <option key={i} value={g}>{g}</option>                          
                    )}
                </select>
            </form>
            <div className="button-container">
                <button className="filter-button" onClick={handleFilterClick}>Filter</button>
                <button className="filter-button" onClick={handleClearClick}>Clear</button>
            </div>
        </div>
    );
};

export default PlayFilters
