import React from "react";

class PlayDataView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {characters: [],
                      acts: []};
    }

    async componentDidMount() {
        if (!this.state.characters.length || !this.state.acts.length) {
            try {
                const url = "https://www.randyconnolly.com//funwebdev/3rd/api/shakespeare/play.php?name=" + this.props.play.id;
                const response = await fetch(url);
                const data = await response.json();
                this.setState({characters: data.persona});
                this.setState({characters: data.acts});
                console.log(data);
            } 
            catch {
                console.error("fetch error");
            }
        }
    }

    render() {
        const props = this.props;
        const play = props.play;

        if (props.currentTab == "Details") {
            return (
                <div className="container">
                    <p><strong>Likely date of composition:</strong> {play.likelyDate}</p>
                    <p><strong>Genre:</strong> {play.genre}</p>
                    <p><strong>Wiki Link:</strong> <a href={play.wiki}>{play.wiki}</a></p>
                    <p><strong>Gutenberg:</strong> <a href={play.gutenberg}>{play.gutenberg}</a></p>
                    <p><strong>Shakespeare.org Link:</strong> <a href={play.shakespeareOrg}>{play.shakespeareOrg}</a></p>
                    <p><strong>Description:</strong></p>
                    <p>{play.desc}</p>
                </div>
            );
        }
        else {
            return (
                <div className="container">
                    <p>Empty</p>
                </div>
            );
        }
    }
};

export default PlayDataView
