import React from "react";
import FavouritesList from "./FavouritesList.js";
import PlayOverview from "./PlayOverview.js";
import PlayDataApp from "./PlayDataApp.js";
import "./css/PlayDetail.css";

const PlayDetail = (props) => {
    var tabs = [];

    if (props.play.filename) {
        tabs = ["details", "characters", "text"];
    }
    else {
        tabs = ["details"];
    }

    return (
        <div className="play-detail hidden-favourites">
            <FavouritesList favourites={props.favourites} removeFromFavourites={props.removeFromFavourites}/>
            <PlayOverview play={props.play} addToFavourites={props.addToFavourites}/>
            <PlayDataApp play={props.play}/>
        </div>
    );
};

export default PlayDetail
