import React from "react";
import FavouritesItem from "./FavouritesItem.js";

const FavouritesList = (props) => {
    const handleToggleClick = () => {
        //toggle
    }

    if (props.favourites) {
        return (
            <div className="container">
                <h1>Favourites</h1>
                {props.favourites.map((p) => 
                    <FavouritesItem key={p.id} play={p} removeFromFavourites={props.removeFromFavourites}/>
                )}
                <p><button onClick={handleToggleClick}>Show/Hide</button></p>
            </div>
        );
    }
    else {
        return null;
    }
};

export default FavouritesList
