import React from "react";
import PlayDataTabBar from "./PlayDataTabBar.js";
import PlayDataView from "./PlayDataView.js";

const PlayDataApp = (props) => {
    const [currentTab, setCurrentTab] = React.useState("details");
    const [playData, setPlayData] = React.useState(props.play.desc);

    const updatePlayData = (tab) => {
        var newPlayData = "";
        if (tab === "details") {
            newPlayData += "Likely date of composition: " + props.play.likelyDate;
            newPlayData += "Genre: " + props.play.genre;
            newPlayData += "Wiki Link: " + props.play.wiki;
            newPlayData += "Gutenberg: " + props.play.gutenberg;
            newPlayData += "Shakespeare.org Link: " + props.play.shakespeareOrg;
            newPlayData += "Description: \n" + props.play.desc;
        }

        setCurrentTab(tab);
        setPlayData(newPlayData);
    }

    return (
        <div className="container">
            <PlayDataTabBar currentTab={currentTab} updatePlayData={updatePlayData}/>
            <PlayDataView data={playData}/>
        </div>
    );
}

export default PlayDataApp
