import React from "react";
import {Link} from 'react-router-dom';

const PlayOverview = (props) => {
    const handleFavouritesClick = () => {
        props.addToFavourites(props.play);
    }

    const handleSelectChange = (e) => {
        const {name, value} = e.selectedTarget;
        props.setPlayFilter(name, value)
    }

    if (props.currentTab === "Text") {
        const acts = props.playData.acts;
        const scenes = props.currentAct.scenes;
        const characters = props.currentScene.speeches.map((s) => s.speaker);

        return (
            <div className="container">
                <h1>{props.play.title}</h1>
                <fieldset id="interface">
                    <select name="act">{acts.map((a) => <option value={a.name}>{a.name}</option>)}</select>
                    <select name="scene">{scenes.map((s) => <option value={s.name}>{s.name}</option>)}</select>
                    <fieldset>
                        <select id="character">{characters.map((c) => <option value={c}>{c}</option>)}</select>      
                        <input type="text" placeholder="Enter a search term" />
                        <button>Filter</button>
                    </fieldset>
                </fieldset>
            </div>
        );
    }
    else {
        return (
            <div className="container">
                <h1>{props.play.title}</h1>
                <p>{props.play.synopsis}</p>
                <Link to="/play-list"><button>Close</button></Link>
                <button onClick={handleFavouritesClick}>â™¥</button>
            </div>
        );
    }
};

export default PlayOverview
