import React from "react";
import {Link} from 'react-router-dom';
import "./css/Home.css";

const Home = (props) => {
    const handleInputChange = (e) => {
        const {name, value} = e.target;
        props.setFilters(name, value);
    };

    const handleMatchingClick = () => {

        props.setFilteredPlays("title");
    }

    const handleAllClick = () => {
        props.setFilteredPlays({}, "title");
    }

    return (
        <div className="home">
            <article className="article">
                <form className="form">
                    <legend>Play Browser</legend>
                    <label>Search Play Title</label><input name="title" type="text"/><br/>
                    <Link to="/play-list"><button onClick={handleOnClick}>Show Matching Plays</button></Link>
                    <Link to="/play-list"><button onClick={handleOnClick}>Show All Plays</button></Link><br/>
                </form>
            </article>
        </div>
    );
};

export default Home
