import React from "react";

const PlayFilters = (props) => {
    const handleOnChange = (e) => {
        const {name, value} = e.target;
        props.setFilters(name, value);

        //toggle checkbox values
        if (e.target.type === "checkbox") {
            if (e.target.value === "on") {
                e.target.value = "off";
            }
            else {
                e.target.value = "on";
            }
        } 
    }

    const handleFilterClick = () => {
        props.setFilteredPlays();
    }

    const handleClearClick = () => {
        props.setFilters();
        props.setFilteredPlays();
    }

    return (
        <div className="container">
            <form>
                <h1>Play Filters</h1>
                <label>Title</label><br/>
                <input name="title" type="text" onChange={handleOnChange} value={props.filters.title}/><br/>
                <label>Year</label><br/>
                <input name="before" type="checkbox" onChange={handleOnChange}/><label>Before</label>
                <input name="beforeYear" type="text" onChange={handleOnChange} value={props.filters.beforeYear}/><br/>
                <input name="after" type="checkbox" onChange={handleOnChange}/><label>After</label>
                <input name="afterYear" type="text" onChange={handleOnChange} value={props.filters.afterYear}/><br/>
                <label>Genre</label>
                <select name="genre" onChange={handleOnChange} value={props.filters.genre}>
                    {props.genres.map((g, i) => 
                        <option key={i} value={g}>{g}</option>                          
                    )}
                </select>
            </form>
            <button onClick={handleFilterClick}>Filter</button>
            <button onClick={handleClearClick}>Clear</button>
        </div>
    );
};

export default PlayFilters
