import React from "react";

const PlayFilters = (props) => {
    var filters = {text:"", before:false, beforeYear:"", after:false, afterYear:"", genre:""};

    const handleOnChange = (e) => {
        const {name, value} = e.target;
        console.log(name);
        filters[name] = value;
    }

    const handleFilterClick = () => {
        props.setFilteredPlays(filters);
    }

    const handleClearClick = () => {
        filters = {text:"", before:false, beforeYear:"", after:false, afterYear:"", genre:""};
        props.setFilteredPlays(filters);
    }

    return (
        <div className="container">
            <form>
                <legend>Play Filters</legend>
                <label>Title</label>
                <input name="text" type="text" onChange={handleOnChange}/>
                <label>Year</label>
                <input name="before" type="radio" onChange={handleOnChange}/><label>Before</label>
                <input name="beforeYear" type="text" onChange={handleOnChange}/>
                <input name="after" type="radio" onChange={handleOnChange}/><label>After</label>
                <input name="afterYear" type="text" onChange={handleOnChange}/>
                <label>Genre</label>
                <select name="genre" onChange={handleOnChange}>
                    {props.genres.map((g) => 
                        <option value={g}>{g}</option>                          
                    )}
                </select>
            </form>
            <button onClick={handleFilterClick}>Filter</button>
            <button onClick={handleClearClick}>Clear</button>
        </div>
    );
};

export default PlayFilters
