import React from "react";
import PlayData from "./PlayData.js";

const PlayDataApp = (props) => {
    var currentTab = "details";
    const handleButtonClick = (e) => {
        currentTab = e.currentTarget.name;
    };

    return (
        <div className="container">
            <button name="details">Details</button>
            <button name="characters">Characters</button>
            <button name="text">Text</button>
            <PlayData currentTab={currentTab} play={props.play}/>
        </div>
    );
};

export default PlayDataApp
