import React from "react";
import logo from './logo.svg';
import './App.css';
import {Route} from 'react-router-dom';
import {cloneDeep, uniq} from 'lodash';
import Header from "./components/Header.js";
import Home from "./components/Home.js";
import PlayList from "./components/PlayList.js";
import PlayDetail from "./components/PlayDetail.js";
import About from "./components/About.js";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {plays: [],
                      currentPlay: null,
                      filteredPlays: [],
                      filters: {title:"", before:"off", beforeYear:"", after:"off", afterYear:"", genre:""},
                      favourites: []};
    }

    async componentDidMount() {
        if (!this.state.plays.length) {
            try {
                const url = "http://www.randyconnolly.com//funwebdev/3rd/api/shakespeare/list.php";
                const response = await fetch(url);
                const data = await response.json();
                this.setState({plays: data});
            } 
            catch {
                console.error("fetch error");
            }
        }
    }

    render() {
        //constants
        const genres = [""];
        genres.push(...uniq(this.state.plays.map((p) => p.genre)));

        //methods
        const setCurrentPlay = (play) => {
            this.setState({currentPlay: play});
        }

        const setFilters = (name, value) => {
            var filtersCopy;

            if (name && value) {
                filtersCopy = {...this.state.filters};
                filtersCopy[name] = value;
            }
            else {
                filtersCopy = {title:"", before:"off", beforeYear:"", after:"off", afterYear:"", genre:""};
            }

            console.log("setFilters");
            console.log(filtersCopy);
            this.setState({filters: filtersCopy});
        }

        const setFilteredPlays = () => {
            const filters = this.state.filters;
            var filteredPlays = cloneDeep(this.state.plays);

            if (filters.title) {
                filteredPlays = filteredPlays.filter((p) => p.title === filters.title);
            }

            console.log("setFilteredPlays");
            console.log(filters);
            this.setState({filteredPlays: filteredPlays});
        }

        const addToFavourites = (play) => {
            const favouritesCopy = [...this.state.favourites];

            //prevent duplicates from being added
            if (!favouritesCopy.find((p) => p.id === play.id)) {
                favouritesCopy.push(play);
            }

            this.setState({favourites:favouritesCopy});
        }

        const removeFromFavourites = (play) => {
            const favouritesCopy = [...this.state.favourites];
            const index = favouritesCopy.findIndex((p) => p.id === play.id);
            favouritesCopy.splice(index, 1);
            this.setState({favourites:favouritesCopy});
        }

        return (
            <div className="App">
                <Route path="/" exact><Home setFilteredPlays={setFilteredPlays}/></Route>
                <Route path="/play-list" exact>
                    <Header/>
                    <PlayList plays={this.state.filteredPlays}
                              favourites={this.state.favourites}
                              setFilters={setFilters}
                              setFilteredPlays={setFilteredPlays} 
                              addToFavourites={addToFavourites}
                              removeFromFavourites={removeFromFavourites}
                              setCurrentPlay={setCurrentPlay}
                              genres={genres}/>
                </Route>
                <Route path="/play-detail" exact>
                    <Header/>
                    <PlayDetail play={this.state.currentPlay}
                                 favourites={this.state.favourites}
                                 addToFavourites={addToFavourites}
                                 removeFromFavourites={removeFromFavourites}/>
                </Route>
                <Route path="/about" exact><About/></Route>
            </div>
        );                
    }
}

export default App;
