import React from "react";
import {Link} from 'react-router-dom';

const Home = (props) => {
    const handleInputChange = (e) => {
        const searchText = e.currentTarget.value;
        const filter = {text:searchText}
        props.setFilteredPlays(filter);
    };

    return (
        <div className="container">
            <article className="article">
                <form className="form">
                    <legend>Play Browser</legend><br/>
                    <label>Title</label><input type="text"/><br/>
                    <Link to="/play-list"><button>Show Matching Plays</button></Link><br/>
                    <Link to="/play-list"><button>Show All Plays</button></Link><br/>
                </form>
            </article>
        </div>
    );
};

export default Home
