import React from "react";
import logo from './logo.svg';
import './App.css';
import {Route} from 'react-router-dom';
import Header from "./components/Header.js";
import Home from "./components/Home.js";
import PlayList from "./components/PlayList.js";
import PlayDetails from "./components/PlayDetails.js";
import About from "./components/About.js";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {plays:[],
                      currentPlay:null,
                      playFilters:[],
                      favourites:[],
                      searchText:""};
    }

    async componentDidMount() {
        if (this.state.plays.isEmpty) {
            try {
                const url = "http://www.randyconnolly.com//funwebdev/3rd/api/shakespeare/list.php";
                const response = await fetch(url);
                const data = await response.json();
                this.setState({plays:data});
            } 
            catch {
                console.error("fetch error");
            }
        }
    }

    render() {
        //methods
        const setCurrentPlay = (play) => {
            this.state({currentPlay:play});
        }

        const setPlayFilters = (filters) => {

        }

        const addToFavourites = (play) => {
            const favouritesCopy = [...this.state.favourites];
            favouritesCopy.push(play);
            this.state({favourites:favouritesCopy});
        }

        const removeFromFavourites = (play) => {
            const favouritesCopy = [...this.state.favourites];
            favouritesCopy.splice(play, 1);
            this.state({favourites:favouritesCopy});
        }

        const setSearchText = (text) => {
            this.state({searchText:text});
        }

        return (
            <div className="App">
                <Route path="/" exact><Home/></Route>
                <Route path="/home" exact><Home/></Route>
                <Route path="/play-list" plays={this.state.plays} exact><PlayList/></Route>
                <Route path="/play-details" exact><PlayDetails/></Route>
                <Route path="/about" exact><About/></Route>
            </div>
        );                
    }
}

export default App;
