import React from "react";
import FavouritesList from "./FavouritesList.js";
import PlayOverview from "./PlayOverview.js";
import PlayData from "./PlayData.js";
import "./css/PlayDetailApp.css";

class PlayDetailApp extends React.Component {
    constructor(props) {
        super(props);
        const tabs = ["Details", "Characters", "Text"];
        this.state = {currentTab: tabs[0],
                      tabs: props.play.filename ? tabs : [tabs[0]],
                      playData: [],
                      currentAct: null,
                      currentScene: null};
    }

    async componentDidMount() {
        const play = this.props.play;

        if (play.filename) {
            const foundPlayData = this.props.playData.find((p) => p.id === play.id);

            if (!foundPlayData) {
                try {
                    const url = "https://www.randyconnolly.com//funwebdev/3rd/api/shakespeare/play.php?name=" + play.id;
                    const response = await fetch(url);
                    const data = await response.json();
                    this.setState({playData: data});
                    this.setState({currentAct: data.acts[0]});
                    this.setState({currentScene: data.acts[0].scenes[0]});
                    this.props.savePlayData(play, data);
                    console.log(data);
                } 
                catch {
                    console.error("fetch error");
                }
            }
            else {
                this.setState({playData: foundPlayData});
                this.setState({currentAct: foundPlayData.acts[0]});
                this.setState({currentScene: foundPlayData.acts[0].scenes[0]});
            }
        }
    }

    render() {
        const setCurrentTab = (tab) => {
            this.setState({currentTab: tab});
        }

        const setCurrentAct = (act) => {
            this.setState({currentAct: act});
        }

        const setCurrentScene = (scene) => {
            this.setState({currentScene: scene});
        }

        return (
            <div className="play-detail hidden-favourites">
                <FavouritesList favourites={this.props.favourites} removeFromFavourites={this.props.removeFromFavourites}/>
                <PlayOverview currentTab={this.state.currentTab} play={this.props.play} characters={this.state.characters} acts={this.state.acts} scenes addToFavourites={this.props.addToFavourites}/>
                <PlayData currentTab={this.state.currentTab} tabs={this.state.tabs} play={this.props.play} characters={this.state.characters} acts={this.state.acts} setCurrentTab={setCurrentTab}/>
            </div>
        );
    }
};

export default PlayDetailApp
