import React from "react";
import FavouritesList from "./FavouritesList.js";
import PlayOverview from "./PlayOverview.js";
import PlayDataApp from "./PlayDataApp.js";
import "./css/PlayDetail.css";

class PlayDetailApp extends React.Component {
    constructor(props) {
        super(props);
        const tabs = ["Details", "Characters", "Text"]
        this.state = {currentTab: tabs[0],
                      tabs: props.play.filename ? tabs : [tabs[0]],
                      characters: [],
                      acts: []};
    }

    async componentDidMount() {
        if (this.props.play.filename && (!this.state.chracters.length || !this.state.acts.length)) {
            try {
                const url = "https://www.randyconnolly.com//funwebdev/3rd/api/shakespeare/play.php?name=" + this.props.play.id;
                const response = await fetch(url);
                const data = await response.json();
                this.setState({characters: data.persona});
                this.setState({characters: data.acts});
                console.log(data);
            } 
            catch {
                console.error("fetch error");
            }
        }
    }

    render() {
        const setCurrentTab = (tab) => {
            this.setState({currentTab: tab});
        }

        return (
            <div className="play-detail hidden-favourites">
                <FavouritesList favourites={this.props.favourites} removeFromFavourites={this.props.removeFromFavourites}/>
                <PlayOverview currentTab={this.state.currentTab} play={this.props.play} addToFavourites={this.props.addToFavourites}/>
                <PlayDataApp currentTab={this.state.currentTab} tabs={this.state.tabs} play={this.props.play}/>
            </div>
        );
    }
};

export default PlayDetailApp
