import React from "react";
import PlayDataTabBar from "./PlayDataTabBar.js";
import PlayDataView from "./PlayDataView.js";

const PlayDataApp = (props) => {

    const [currentTab, setCurrentTab] = React.useState(props.tabs[0]);
    const updateCurrentTab = (tab) => {
        setCurrentTab(tab);
    }

    return (
        <div className="container">
            <PlayDataTabBar tabs={props.tabs} currentTab={currentTab} updateCurrentTab={updateCurrentTab}/>
            <PlayDataView currentTab={currentTab} play={props.play}/>
        </div>
    );
}

export default PlayDataApp
