import React from "react";
import {Link} from 'react-router-dom';

const PlayMatches = (props) => {
    const handleFavouritesClick = (e) => {
        const selectedPlayId = e.currenTarget.name;
        const selectedPlay = props.plays.find((p)=> p.id == selectedPlayId);
        props.addToFavourites(selectedPlay);
    }

    const handleViewClick = (e) => {
        const selectedPlayId = e.currenTarget.name;
        const selectedPlay = props.plays.find((p)=> p.id == selectedPlayId);
        props.setCurrentPlay(selectedPlay);
    }

    return (
        <div className="container">
            <h1>List/Matches</h1>
            <table>
                <thead>
                    <td>Title</td>
                    <td>Year</td>  
                    <td></td>
                    <td></td>
                </thead>
                <tbody>
                {props.plays.map((p) => 
                    <tr key={p.id}>
                        <td>{p.title}</td>
                        <td>{p.year}</td>
                        <td><Link to="/play-detail"><button name={p.id} onClick={handleFavouritesClick}>Fave</button></Link></td>
                        <td><button name={p.id} onClick={handleViewClick}>View</button></td>
                    </tr>
                )}
                </tbody>
            </table>
        </div>
    );
};

export default PlayMatches
